/*
<summary> Finds if elementLeft is the same as elementRight.        e.g. elementLeft == elementRight within pareto context. </summary> <param name='elementLeft'>The element at the left of the operator</param> <param name='elementRight'>The element at the right of the operator</param> <param name='propertyArray'>The array of properties that will be pareto tested</param> <param name='tolerance'>value of tolerance</param> <return>True if equal, false otherwise</return>
/
function _paretoEqual(elementLeft, elementRight, propertyArray, tolerance) {
for (var ii = 0 ; ii < propertyArray.length; ++ii) {        if (Math.abs(elementLeft[propertyArray[ii]] - elementRight[propertyArray[ii]]) > tolerance)            return false;    }    return true;
}
