LinkDrawer.prototype.shouldWrapInDim = function(canWrap, dimensionSize, cor1, cor2) {      var distance;      distance = Math.abs(cor1 - cor2);      return canWrap && distance > dimensionSize / 2;    };
LinkDrawer.prototype.calculateShortestLineAngle = function(x1, y1, x2, y2) {      var shortestX, shortestY;      shortestX = Math.min(x1 - x2, x2 - x1);      shortestY = Math.min(y1 - y2, y2 - y1);      return Math.atan2(shortestY, shortestX);    };
LinkDrawer.prototype.calculateComps = function(x1, y1, x2, y2, size) {      var xcomp, ycomp;      xcomp = (y2 - y1) / size;      ycomp = (x1 - x2) / size;      return [xcomp, ycomp];    };
LinkDrawer.prototype.calculateSublineOffset = function(centerOffset, thickness, xcomp, ycomp) {      var thicknessFactor, xOff, yOff;      thicknessFactor = thickness / this.view.onePixel;      xOff = centerOffset * thicknessFactor * xcomp;      yOff = centerOffset * thicknessFactor * ycomp;      return [xOff, yOff];    };

