.aboutLabel, .aboutText {
font-family: 'Open Sans Condensed', sans-serif;
color: #fff;
text-shadow: 0px 0px 1px white;
font-size: 14px;
}
.aboutLabel {
text-transform: uppercase;
font-size: 16px;
font-style: italic;
margin-top: 12px;
font-weight: bold;
}
.va-slice ul{
margin-top:20px;
}
.va-slice ul li{
float:left;
margin:0px 2px;
}
.va-slice ul li a{
color:#000;
background:#eede2f;
padding:3px 6px;
font-size:14px;
font-family:'PT Sans', sans-serif;
text-transform:uppercase;
}
.va-slice ul li a:hover{
background:#000;
color:#fff;
text-shadow:none;
}
.va-nav span{
width:40px;
height:25px;
background:transparent no-repeat center center;
position:absolute;
top:-35px;
left:50%;
margin-left:-20px;
text-indent:-9000px;
opacity:0.7;
cursor:pointer;
display:none;
z-index:100;
}
.va-nav span.va-nav-next{
top:auto;
bottom:-35px;
}
.va-nav span:hover{
opacity:1.0;
}
