/* animated spoiler CSS by Bloggersentral.com (because I was too lazy to make my own )-: */ .spoilerbutton {  display:block;  margin:5px 0;  font-size: 1.1em !important; } .spoiler {  overflow:hidden;  background: #f5f5f5; } .spoiler > div {  -webkit-transition: all 0.2s ease;  -moz-transition: margin 0.2s ease;  -o-transition: all 0.2s ease;  transition: margin 0.2s ease; } .spoilerbutton[value="Show"] + .spoiler > div {  margin-top:-300%; } .spoilerbutton[value="Hide"] + .spoiler {  padding:5px; }
