function _paretoGreaterThan(elementLeft, elementRight, propertyArray, maximizeArray) {
for (var ii = 0 ; ii < propertyArray.length; ++ii)    {        if (maximizeArray[ii])        {            if (elementLeft[propertyArray[ii]] < elementRight[propertyArray[ii]])            {                return false;            }        }        else        {            if (elementLeft[propertyArray[ii]] > elementRight[propertyArray[ii]])            {                return false;            }        }    }    return true;
}
function ParetoFrontRank(array, propertyArray, maxMinArray, rank) {
//Shallow copy array    var queueArray = array.slice();    do {        var frontIndexes = new Array();        for (var ii = 0; ii < queueArray.length ; ++ii) {            var testElement = queueArray[ii];            var testElementDominated = false;            for (var jj = 0; jj < queueArray.length; ++jj) {                if (ii == jj) {                    continue;//do not test an element against itself                }                else {                    //an element is rank 0 if no one dominates him. Check if any element dominates him                    var dominated = _paretoGreaterThan(queueArray[jj], testElement, propertyArray, maxMinArray);                    if (dominated) {                        var equal = _paretoEqual(queueArray[jj], testElement, propertyArray, 0.01);                        //an object dominates itself since it is no better at anything than itself. Identical objects must be caught                        if (!equal) {                            testElementDominated = true;                            break;                        }                    }                }            }
