.list-unstyled {
padding-left: 0;  list-style: none;
}
.list-inline {
padding-left: 0;  list-style: none;
}
.list-inline-item {
display: inline-block;
}
.list-inline-item:not(:last-child) {
margin-right: 5px;
}
.initialism {
font-size: 90%;  text-transform: uppercase;
}
.blockquote {
margin-bottom: 1rem;  font-size: 1.25rem;
}
.blockquote-footer {
display: block;  font-size: 80%;  color: #868e96;
}
.blockquote-footer::before {
content: "\2014 \00A0";
}
.img-fluid {
max-width: 100%;  height: auto;
}
.img-thumbnail {
padding: 0.25rem;  background-color: #fff;  border: 1px solid #ddd;  border-radius: 0.25rem;  transition: all 0.2s ease-in-out;  max-width: 100%;  height: auto;
}
.figure {
display: inline-block;
}
.figure-img {
margin-bottom: 0.5rem;  line-height: 1;
}
.figure-caption {
font-size: 90%;  color: #868e96;
}
code, kbd, pre, samp {
font-family: Menlo, Monaco, Consolas, "Liberation Mono", "Courier New", monospace;
}
code {
padding: 0.2rem 0.4rem;  font-size: 90%;  color: #bd4147;  background-color: #f8f9fa;  border-radius: 0.25rem;
}
a > code {
padding: 0;  color: inherit;  background-color: inherit;
}
kbd {
padding: 0.2rem 0.4rem;  font-size: 90%;  color: #fff;  background-color: #212529;  border-radius: 0.2rem;
}
kbd kbd {
padding: 0;  font-size: 100%;  font-weight: bold;
}
pre {
display: block;  margin-top: 0;  margin-bottom: 1rem;  font-size: 90%;  color: #212529;
}
pre code {
padding: 0;  font-size: inherit;  color: inherit;  background-color: transparent;  border-radius: 0;
}
.pre-scrollable {
max-height: 340px;  overflow-y: scroll;
}
