Chart.Type.extend({   name: "Bar",   defaults : defaultConfig,   initialize:  function(data){
//Expose options as a scope variable here so we can access it in the ScaleClass    var options = this.options;
this.ScaleClass = Chart.Scale.extend({     offsetGridLines : true,     calculateBarX : function(datasetCount, datasetIndex, barIndex){      //Reusable method for calculating the xPosition of a given bar based on datasetIndex & width of the bar      var xWidth = this.calculateBaseWidth(),       xAbsolute = this.calculateX(barIndex) - (xWidth/2),       barWidth = this.calculateBarWidth(datasetCount);
return xAbsolute + (barWidth * datasetIndex) + (datasetIndex * options.barDatasetSpacing) + barWidth/2;     },     calculateBaseWidth : function(){      return (this.calculateX(1) - this.calculateX(0)) - (2*options.barValueSpacing);     },     calculateBarWidth : function(datasetCount){      //The padding between datasets is to the right of each bar, providing that there are more than 1 dataset      var baseWidth = this.calculateBaseWidth() - ((datasetCount - 1) * options.barDatasetSpacing);
return (baseWidth / datasetCount);     }    });
this.datasets = [];
//Set up tooltip events on the chart    if (this.options.showTooltips){     helpers.bindEvents(this, this.options.tooltipEvents, function(evt){      var activeBars = (evt.type !== 'mouseout') ? this.getBarsAtEvent(evt) : [];
this.eachBars(function(bar){       bar.restore(['fillColor', 'strokeColor']);      });      helpers.each(activeBars, function(activeBar){       activeBar.fillColor = activeBar.highlightFill;       activeBar.strokeColor = activeBar.highlightStroke;      });      this.showTooltip(activeBars);     });    }
//Declare the extension of the default point, to cater for the options passed in to the constructor    this.BarClass = Chart.Rectangle.extend({     strokeWidth : this.options.barStrokeWidth,     showStroke : this.options.barShowStroke,     ctx : this.chart.ctx    });
