text:
  padding: 0;  text-align: center;  text-decoration: none;  color: #333;
} .thumbnail .img {
display: block;  width: 100%;  overflow: hidden;  height: 180px;  margin: 0 0 20px;  position: relative;
} .thumbnail .title {
text-transform: uppercase;  margin: 0 2em;  display: block;  text-indent: 0;
} .thumbnail .cover {
position: absolute;  display: block;  opacity: 0;  width: 100%;  height: 180px;  background-color: rgba(255, 255, 255, 0.8);  -webkit-transition-property: all;  -moz-transition-property: all;  -o-transition-property: all;  -ms-transition-property: all;  transition-property: all;  -webkit-transition-duration: 550ms;  -moz-transition-duration: 550ms;  -o-transition-duration: 550ms;  transition-duration: 550ms;
} .thumbnail .cover .more {
position: absolute;  bottom: 15px;  right: 0;  height: 32px;  line-height: 32px;  padding: 0 20px;  -webkit-border-radius: 1px 0 0 1px;  -webkit-background-clip: padding-box;  -moz-border-radius: 1px 0 0 1px;  -moz-background-clip: padding;  border-radius: 1px 0 0 1px;  background-clip: padding-box;  font-size: 12px;  font-size: 0.75rem;  text-transform: uppercase;  text-shadow: 0 1px 0px #780d33;  background: #bd1550;  color: white;
} .thumbnail img {
width: 100%;  position: absolute;  top: 0;  left: 0;
} .thumbnail:hover {
color: #333;  text-decoration: none;
} .thumbnail:hover img {
-webkit-filter: saturate(0%);
} .thumbnail:hover .cover {
opacity: 1;
} /* pagination */ .pagination > li > a {
color: #808080;  border: 0 none;  font-size: 20px;  font-size: 1.25rem;  font-family: Georgia, serif;
} .
