.breadcrumb {
padding: 0.75rem 1rem;  margin-bottom: 1rem;  list-style: none;  background-color: #e9ecef;  border-radius: 0.25rem;
}
.breadcrumb::after {
display: block;  clear: both;  content: "";
}
.breadcrumb-item {
float: left;
}
.breadcrumb-item + .breadcrumb-item::before {
display: inline-block;  padding-right: 0.5rem;  padding-left: 0.5rem;  color: #868e96;  content: "/";
}
.breadcrumb-item + .breadcrumb-item:hover::before {
text-decoration: underline;
}
.breadcrumb-item + .breadcrumb-item:hover::before {
text-decoration: none;
}
.breadcrumb-item.active {
color: #868e96;
}
.pagination {
display: -ms-flexbox;  display: flex;  padding-left: 0;  list-style: none;  border-radius: 0.25rem;
}
.page-item:first-child .page-link {
margin-left: 0;  border-top-left-radius: 0.25rem;  border-bottom-left-radius: 0.25rem;
}
.page-item:last-child .page-link {
border-top-right-radius: 0.25rem;  border-bottom-right-radius: 0.25rem;
}
.page-item.active .page-link {
z-index: 2;  color: #fff;  background-color: #007bff;  border-color: #007bff;
}
.page-item.disabled .page-link {
color: #868e96;  pointer-events: none;  background-color: #fff;  border-color: #ddd;
}
.page-link {
position: relative;  display: block;  padding: 0.5rem 0.75rem;  margin-left: -1px;  line-height: 1.25;  color: #007bff;  background-color: #fff;  border: 1px solid #ddd;
}
.page-link:focus, .page-link:hover {
color: #0056b3;  text-decoration: none;  background-color: #e9ecef;  border-color: #ddd;
}
