isValidValue = function(widget, value) {    return (value != null) && (function() {      switch (widget.type) {        case 'slider':          return !isNaN(value);        case 'inputBox':          return !(widget.boxedValue.type === 'Number' && isNaN(value));        default:          return true;      }    })();  };
