.advisor, {
margin-bottom: 12px;
}
bodyContent .button2 {
text-align: center;    margin-top: 70px;    margin-bottom: 20px;
}
.button2 a {
color: #fff;    text-decoration: none;    background-color: #8F3931;    padding: 10px 60px;    text-align: center;    vertical-align: middle;    border-radius: 5px;    -webkit-border-radius: 5px;    transition: background 0.1s ease-in-out;    -webkit-transition: background 0.1s ease-in-out;
}
.button2:hover a {
background-color: #800000;
}
.button2 a:hover {
color: #D6D6CE;    text-decoration: none;
}
.dividerNotebook {
width: 760px;    margin: 0 auto;    margin-top: -20px;
}
.dividerNotebook p {
border-top: 1px solid #D6D6CE;    text-align: right;    font: 10px Verdana;    padding: 2px 2px 20px 0;
}
.dividerNotebook p a, .divider p a:hover {
color: #800000;    text-decoration: none;
}
bodyContent .button3 {
text-align: center;    margin-top: 70px;    margin-bottom: 20px;
}
.button3 a {
color: #8A9045;    text-decoration: none;    background-color: #767676;    padding: 10px 60px;    text-align: center;    vertical-align: middle;    border-radius: 5px;    -webkit-border-radius: 5px;    transition: all 0.1s ease-in-out;    -webkit-transition: all 0.1s ease-in-out;
}
.button3:hover a {
background-color: #D6D6CE;
}
.button3 a:hover {
color: #58593F;    text-decoration: none;
}
