menu {
text-align: left;  position: fixed;  top: 50px;  height: auto;  width: auto;  z-index: 999;  height: 500px;
opacity:0.9;
}
menu ul li a.selected {
color: red;  background: silver; } /*Toggle Area*/
menu .toggle {
float: right;  width: 9px;  padding: 5px;  cursor: pointer;  border-top: 1px solid white;  border-left: 1px solid #204757;  color: #CCD1FF;  text-indent: 0;  margin-left: 0; }
menu ul.navmenu li:first-child .toggle {
border-width: 0 0 0 1px; } /*Menu Setup*/
menu ul {
padding: 0;  margin: 0;  width: 150px; }
menu ul ul {
border: 1px solid #CCD1FF;  overflow: hidden; }
menu ul.navmenu li {
margin: 0;  list-style: none;  float: left; }
menu ul.navmenu li li {
float: none; } /*Links*/
menu ul.navmenu a, #menu ul.navmenu a:visited {
text-decoration: none;  padding: 5px;  display: block;  color: #EEFBFF; }
menu ul.navmenu ul.submenu a:hover {
background: #1B85CB;  color: #EEF0FF; } /*Heading Outer div*/
menu ul.navmenu .menutop {
border: 1px solid #CCD1FF;  border-width: 0 1px;  overflow: hidden;  width: 150px;  background: #404040; } /*Header Links*/
menu ul.navmenu .menutop a {
width: 120px;  float: left;  margin: 0 0 1px 0;  border-top: 1px solid white; } /*Header Link Hover*/
menu ul.navmenu .menutop a:hover {
color: #F7F8FF; } /*Removes white border for the first header*/
menu ul.navmenu li:first-child .menutop a {
border-width: 0px; } /*Single Menu Width Fix*/
menu ul.navmenu .menusingle a {
width: 140px; } /*Border Radius and Special Border Width*/
menu ul.navmenu li:first-child .menutop {
border-width: 1px 1px 0 1px;  -moz-border-radius: 5px 5px 0 0;  -webkit-border-top-left-radius: 5px;  -webkit-border-top-right-radius: 5px; }
menu ul.navmenu li:last-child .menutop {
border-width: 0px 1px 1px 1px;  -moz-border-radius: 0 0 5px 5px;  -webkit-border-bottom-left-radius: 5px;  -webkit-border-bottom-right-radius: 5px; }
menu ul.navmenu li:last-child ul.submenu {
-moz-border-radius: 0 0 5px 5px;  -webkit-border-bottom-left-radius: 5px;  -webkit-border-bottom-right-radius: 5px; }
menu ul.navmenu li:last-child .menutop-open {
-moz-border-radius: 0;  -webkit-border-radius: 0px;  border-width: 0 1px; }
