}
.service-details img {  position: absolute;  top: 0;  left: 0;  height: inherit;
height: 100%;  float: left;  transition: all 0.8s;  -moz-transition: all 0.8s;
border: none;
}
.service-details:hover img {  /*opacity: 0.4 !important;*/
border: none;
}
.service-details .service-hover-text h3 {  padding: 0px;  margin: 0px;  font-size: 25px;
font-weight:300;        font-family: 'Indie Flower', cursive;        text-align: left;
}
.service-details .service-hover-text h4 {  padding: 0px;  padding-bottom: 13px;  margin: 0px;  font-size: 14px;
letter-spacing: 3px;
width: 90%;  font-family: "Open Sans";
text-transform:uppercase;
border-bottom: 2px solid #000; }
.service-details .service-hover-text p {  padding-top: 13px;  font-size: 14px;  line-height: 20px;  font-family: "Open Sans"; }
.service-details .service-hover-text{  width: 50%;  height: 100%;  position: absolute;  top: 0%;  left: 50%;  padding: 3% 4%;  background: #D90E0E;  color: rgba(255,255,255,1); /* display: none;*/  transition: all 0.5s ease-in-out;  -moz-transition: all 0.4s; }
.service-details:hover .service-hover-text {  display: block !important;  color: rgba(255,255,255,1);  background:rgba(217,14,14,0.85);  left: 0px;  top: 0px;
