}
.service-details .service-text {  width: 50%;  height: inherit;  background: #000;  float: left;  position: absolute;  left: 50%; }
.service-details .service-text p {  padding:200px 0px 0px 20px;  font-size: 35px;  font-family: "Open Sans";
font-weight:700;
color: #fff; }
.service-details .service-text p span {  font-family: "Open Sans" !important; }
.service-details .service-text a , .service-white .service-text {  padding: 0px 0px 0px 20px;  font-size: 15px !important;  color:#FF5A22 !important;  /*font-family: "Open Sans" !important;*/
font-family: 'Indie Flower', cursive !important;
text-decoration: none !important;
