}
function updateSlick(height){
elementHeight = $('#memberSelector .row:first').outerHeight();  numElements = Math.floor(height/elementHeight);  memberselector = $('#memberSelector');  memberselector.slickSetOption("slidesToShow", numElements, false);  memberselector.slickSetOption("slidesToScroll", numElements, true);
}
