$('.imageBorder').height($('#memberImage').height());  $('#memberImageOverlay').attr("src", src);  $('#memberImageOverlay').load(function(){    $(this).fadeIn(400, "swing", function(){      $('#memberImage').attr("src", src);      $('#memberImage').load(function(){        $('#memberImageOverlay').hide();        $('.imageBorder').css("height", "");      });    });
});
}
