$('#Teamfoto-btn').click(function(){    if(teamView == false){      $('.team-overlay').css("display", "block");      $('.team-overlay').animate({ opacity: 1}, 200);      $('.memberview').animate({ opacity: 0}, 200);      teamView = true;      updateHeights();    }  });
memberselector = $('#memberSelector');
